) {","    this.getAttribute('id');","  }","","  formats() {","    // We don't inherit from FormatBlot","    return { [this.statics.blotName]:","      this.domNode.getAttribute('table_id') + '|' +","      this.domNode.getAttribute('row_id') + '|' +","      this.domNode.getAttribute('cell_id') }","  }","","  optimize() {","    super.optimize();","","    // Add parent TR and TABLE when missing","    let parent = this.parent;","    if (parent != null && parent.statics.blotName != 'tr') {","      // we will mark td position, put in table and replace mark","      let mark = Parchment.create('block');","      this.parent.insertBefore(mark, this.next);","      let table = Parchment.create('table', this.domNode.getAttribute('table_id'));","      let tr = Parchment.create('tr', this.domNode.getAttribute('row_id'));","      table.appendChild(tr);","      tr.appendChild(this);","      table.replace(mark)","    }","","    // merge same TD id","    let next = this.next;","    if (next != null && next.prev === this &&","        next.statics.blotName === this.statics.blotName &&","        next.domNode.tagName === this.domNode.tagName &&","        next.domNode.getAttribute('cell_id') === this.domNode.getAttribute('cell_id')) {","      next.moveChildren(this);","      next.remove();","    }","  }","}","","TableCell.blotName = 'td';","TableCell.tagName = 'td';","TableCell.scope = Parchment.Scope.BLOCK_BLOT;","TableCell.defaultChild = 'block';","TableCell.allowedChildren = [Block, BlockEmbed, Container];","Quill.register(TableCell);","TableRow.allowedChildren = [TableCell];","","Container.order = [","  'list', 'contain',   // Must be lower","  'td', 'tr', 'table'              // Must be higher","];"],"id":110}],[{"start":{"row":36,"column":4},"end":{"row":37,"column":0},"action":"remove","lines":["",""],"id":111}],[{"start":{"row":36,"column":0},"end":{"row":37,"column":0},"action":"remove","lines":["    ",""],"id":112}],[{"start":{"row":9,"column":0},"end":{"row":10,"column":0},"actio